function Course(){}Course.prototype.ueInit=function(){window.ue1=UE.getEditor("course_desc",{initialFrameHeight:100,serverUrl:"/ueditor/upload/"}),console.log(window.ue1)},Course.prototype.listenCourseSubmitEvent=function(){$("#course-confirm-btn").click(function(e){e.preventDefault();var o=$("#course_title").val(),t=$("#course_category option:selected").val(),n=$("#course_teacher option:selected").val(),c=$("#course_link").val(),u=$("#course_time").val(),s=$("#course_image").val(),i=$("#course_price").val(),r=window.ue1.getContent();console.log(o,c,u,s,i,r),bufanajax.post({url:"/cms/course/",data:{title:o,category:t,teacher:n,link:c,times:u,cover:s,price:i,abstract:r},success:function(e){200===e.code&&swal("成功","成功添加文章","success")},fail:function(e){console.log(e)}})})},Course.prototype.coveruploadEvent=function(){$("#upload-file").change(function(){var e=this.files[0],o=new FormData;o.append("file",e),bufanajax.post({url:"/cms/upload/",processData:!1,contentType:!1,data:o,success:function(e){console.log(e),console.log(e.data.file_url),200===e.code&&$("#course_image").val(e.data.file_url)},fail:function(e){console.log(e)}})})},Course.prototype.run=function(){this.ueInit(),this.listenCourseSubmitEvent(),this.coveruploadEvent()},$(function(){(new Course).run()});